        $scope.contacts = {};
        $scope.muted = false;
        $scope.showVedio = false;
        // 显示已通话时间
        // $scope.VedioTime = 0;
        // $interval(function () {
        //     $scope.VedioTime++;
        // }, 1000);
        $scope.contactUser = {};
        // === 仅供作为测试用(注释部分) begin====
        // var contactName_c = CacheFactory.get('onlyguy');
        // if (contactName_c && $stateParams.contactName === "kobepeng") {
